import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import {Layout, Header, Navigation, Drawer, Content} from 'react-mdl';
import 'typeface-roboto';
import {Grid, Cell} from 'react-mdl';
class App extends Component {
  constructor(props) {
    super(props);
    this.textSeq = this.textSeq.bind(this);
  }
  textSeq(i) {
    if(music_title.length > i) {
      setTimeout(function() {
        document.getElementById("title-text").innerHTML = music_title[i];
        textSeq(i++);
      },1000)
    }
    else if(music_title.length == i) {
      textSeq(0);
    }
  }
  render() {
    return (
      <div className="demo-big-content">
        <Layout>
            <Header className="header-color" title="Revive" scroll>
                <Navigation>
                    <a href="#home">Home</a>
                    <a href="#about">About</a>
                    <a href="#contact">Contact</a>
                </Navigation>
            </Header>
            <Content>
                {/* <div className="page-content" /> */}
                <section className="about" id="about" style={{width:'100%', margin: 'auto'}}>
                  <Grid className="about-grid">
                  {/* <h1>About</h1> */}
                    <Cell col={12}>
                      <img 
                        src="https://purepng.com/public/uploads/large/purepng.com-play-music-icon-android-kitkatsymbolsiconsapp-iconsandroid-kitkatandroid-44-721522597670k5zh5.png"
                        alt="music"
                        className="music-img"
                      />
                      <div className="title-text" id="title-text">
                        {/* {
                          this.props.music_title.map(e => <h1>{e}</h1>)
                        }
                         */}
                        <hr />

                       <div className="music-icon">
                          <img  
                            src="https://image.flaticon.com/icons/svg/189/189889.svg"
                            alt="pause"
                            onClick="PlayOn(element)"
                          />
                          <img
                            src="https://image.flaticon.com/icons/svg/189/189888.svg"
                            alt="next"
                          />
                          <img 
                          src="https://image.flaticon.com/icons/svg/189/189890.svg"
                          alt="play"
                          />

                        </div>

                      </div>
                    </Cell>
                  </Grid>
                </section>

                <section className="contact" id="contact">
                  <h1>Contact</h1>
                </section>
            </Content>
        </Layout>
      </div>
    );
  }
}

export default App;
